import axios from "axios";
export default {
  methods: {
    requestPay: function () {
      const {
        IMP
      } = window;
      IMP.init('imp70364071');
      // https://developers.portone.io/docs/ko/sdk/javascript-sdk/payrq >> 파라미터 정리 되어있음.
      IMP.request_pay({
        // param
        pg: "kcp.INIBillTst_inicis",
        // pg사 구분코드
        pay_method: "card",
        // 결제수단 구분코드
        merchant_uid: "vrm_wando_0616_20231031_9",
        // 가맹점 주문번호. 40bytes 이하. 매 거래시 다 달라야함. 거래고유코드라고 생각해도 될듯?
        name: "wando 결제테스트",
        // 결제대상 제품명 > 상점명(storename + 예약) 으로 바꿔야할듯
        amount: 50,
        // 결제 금액@@@@
        buyer_email: "ourbaby7@nate.com",
        // 주문자 이메일  > default로 저게 박힘
        buyer_name: "김도완",
        // 주문자 이름@@@@ > 김 * 완 으로 박혀있음
        buyer_tel: "010-1234-5678",
        // 주문자 전화번호
        buyer_addr: "서울특별시 강남구 신사동",
        // 주문자 주소
        buyer_postcode: "01181" // 주문자 우편번호
      }, rsp => {
        // callback
        if (rsp.success) {
          console.log("rsp.success => " + rsp);

          // axios로 HTTP 요청
          const paymentData = {
            payment_id: rsp.imp_uid,
            // 포트원 고유 결제번호. success가 false이고 사전 validation에 실패한 경우, imp_uid는 null일 수 있음
            storename: rsp.merchant_uid,
            userid: rsp.name,
            price: rsp.paid_amount,
            status: rsp.status
          };
          axios.post("/api/payment", paymentData).then(data => {
            // 서버 결제 API 성공시 로직
            console.log(data);
            if (data.payment_id != null) alert(data.payment_id + " 결제 성공!");
            this.$router.push({
              name: "PaymentSuccess"
            });
          }).catch(error => {
            // 서버 결제 API 실패시 로직
            console.log(error);
            console.log(error.toString());
            alert("서버 결제 API 요청 실패");
          });
        } else {
          console.log("rsp.failed" + rsp);
          console.log("rsp.failed" + rsp.error_msg);
          alert("결제에 실패했습니다. 에러 내용 : " + rsp.error_msg);
        }
      });
    }
  }
};
