Mypage.vue

<script>

import {reactive} from "vue";
import axios from "axios";
import Header from "@/components/header/Header.vue";

export default {
  name: "Mypage",
  components: {Header},
  setup() {
    const state = reactive({
      items: Object
    })

    const load = () => {
      axios.get("/api/user/mypage").then(({data}) => {
        state.items = data;
      })
    };

    load();
    return {state};
  }
}

</script>

